        })
        .then((res) => res.json())
        .then((data) => {
            console.log(data, "userData")
            setData(data)
        })
    }, [])

    const content = ( 
        <>
            <table>
                <tr>
                    <th>Name</th>
                    <th>Team</th>
                    <th>Age</th>
                </tr>
                {data.map( i => {
                    return (<tr>
                                <th>{i.Name}</th>
                                <th>{i.Team}</th>
                                <th>{i.Age}</th>
                            </tr>)
                })}
            </table>
        </>
    )
    return content  
}
