import { set } from "mongoose";
import React, { useState, useEffect } from "react";

function PostData(){
    const [formvalue, setFormvalue] = useState({name: '', team: '', age:''} )

    const submitForm = (e) => {
        e.preventDefault()
        useEffect( () => {
            fetch('http://localhost:1234/staff', {
                method: 'POST',
                body: {name, team, age}
            })
            .then((res) => res.json())
            .then((data) => {
                console.log(data, "userData")
                setData(data)
            })
        }, [])

    }

    return (<form>
        <label> Name: 
        <input type="text" name="Name"  value = {formvalue.name} onChange={(e) => setName(e.target.value)}/>
        </label>
        Team: <select id="Team" name="Team" value = {formvalue.name}  onChange={(e) => setTeam(e.target.value)}>
            <option value="Marketing">Marketing</option>
            <option value="Accounting">Accounting</option>
            <option value="Sales">Sales</option>
            <option value="Operations">Operations</option>
        </select>
        Age: <input type="number" name="Age" value = {formvalue.name} onChange={(e) => setAge(e.target.value)}/>
        <input type="submit" onClick= {submitForm} name="submit"></input>

    </form>)
}
function GetData(){
    const [data, setData] = useState([]);

    useEffect( () => {
        fetch('http://localhost:1234/staff', {
            method: 'GET',
        })
        .then((res) => res.json())
        .then((data) => {
            console.log(data, "userData")
            setData(data)
        })
    }, [])

    const content = ( 
        <>
            <table>
                <tr>
                    <th>Name</th>
                    <th>Team</th>
                    <th>Age</th>
                </tr>
                {data.map( i => {
                    return (<tr>
                                <th>{i.Name}</th>
                                <th>{i.Team}</th>
                                <th>{i.Age}</th>
                            </tr>)
                })}
            </table>
        </>
    )
    return content  
}
export default function Staff(){
   return   <>
            <GetData/>
            <PostData/>
            </>

}
